        table_row += '<td class="amount">' + amount + '</td>';

        // if(itemid == 'group') {
        //     table_row += '<td></td>';
        // } else {
        table_row += '<td><a href="#" class="btn btn-danger pull-right" onclick="delete_item(this,' + itemid + '); return false;"><i class="fa fa-times"></i></a></td>';
        //}

