(2223, 'meetings', 'meetings-send-notification', 'english', 'Send Meeting Notification', '{meeting_name} - Meeting Invite', '<span style=\"font-size: 12pt;\">Dear {meeting_attendees},</span><br /> <br /><span style=\"font-size: 12pt;\">{meeting_description}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>When:</strong> <strong>{meeting_start}</strong> to <strong>{meeting_end}</strong></span><span style=\"font-size: 12pt;\"></span><span style=\"font-size: 12pt;\"><strong></strong></span><br /> <strong>Timezone: </strong>{meeting_zone}<strong><br /></strong> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{companyname}</span>', '{companyname}', '', 0, 1, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2224, 'messages', 'new-message-created', 'english', 'Message Created Notification', 'New message created - {message_subject}', '{message_privacy}<br />{message_thread}<br />{message_details}', '{message_from}', '', 0, 1, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2225, 'projects', 'invite-new', 'english', 'New Invite', 'Vendor invite pending approval for {project_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is a vendor invite pending your approval. Details below:<br /><br /><bold>Vendor details:</bold><br/><br/>Name: {vendor_name}<br/><br/>Phone: {vendor_phone}<br/><br/>Email: {vendor_email}<br/><br/>Tag: {vendor_tag}<br/><br/>Inviting Vendor/Team Member details:<br/><br/>Name: {invited_name}Email: {invited_email}<br/><br/>{project_details}<br/><br/>Please click on the \"View Invite\" button to view the details and Accept / Decline the invite. <a href=\"{invite_link}\">View Invite</a> {email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2226, 'projects', 'invite-vendor', 'english', 'Account owner approves invite', 'Event invitation for {project_type} on {project_date}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is an event planned on {project_date} for which we would like you to be part of. As a vendor, we would like your company to provide your services for this event. Event details below:<br/><br/>{project_details}<br/><br/>Please click on the <a href=\"{invite_link}\">View Invite</a> to view the details and Accept / Decline the invite. If you have any questions or concerns, please do not hesitate to contact at:<br/><br/>Name: {invited_name}<br/><br/>Phone: {invited_phone}<br/><br/>Email: {invited_email}<br/><br/>Sincerely,<br/><br/>{name}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2227, 'projects', 'invite-new-vendor', 'english', 'Account owner approves invite', 'Event invitation for {project_type} on {project_date}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is an event planned on {project_date} for which we would like you to be part of. As a vendor, we would like your company to provide your services for this event. Event details below:<br/><br/>{project_details}<br/><br/>Please click on the <a href=\"{invite_link}\">View Invite</a> to view the details and Accept / Decline the invite. You can login with following credentials: <br/><br/>Email: {vendor_email}<br/><br/>Password: {vendor_password}<br/><br/> If you have any questions or concerns, please do not hesitate to contact at:<br/><br/>Name: {invited_name}<br/><br/>Phone: {invited_phone}<br/><br/>Email: {invited_email}<br/><br/>Sincerely,<br/><br/>{vendor_name}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2228, 'projects', 'decline-invite', 'english', 'Account owner declines invite', 'Event invitation declined for {vendor_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> {account_name} has declined your invite for:<br/><br/>Name: {vendor_name}<br/><br/>{project_details}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2229, 'projects', 'decline-vendor', 'english', 'Vendor declines invite', 'Event invitation declined by {vendor_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> {vendor_name} has declined your invite for:<br/><br/>{project_details}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 51, 0, 89, '2018-01-12 23:53:38', NULL, NULL),
(2230, 'client', 'new-client-created', 'english', 'New Contact Added/Registered (Welcome Email)', 'Welcome aboard', 'Dear {contact_firstname} {contact_lastname}<br /><br />Thank you for registering on the <strong>{companyname}</strong> CRM System.<br /> <br />We just wanted to say welcome.<br /> <br />Please contact us if you need any help.<br /> <br />Click here to view your profile: <a href=\"{crm_url}\">{crm_url}</a><br /> <br />Kind Regards, <br />{email_signature}<br /> <br />(This is an automated email, so please don\'t reply to this email address)', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2231, 'invoice', 'invoice-send-to-client', 'english', 'Send Invoice to Customer', 'Invoice with number {invoice_number} created', '<span style=\"font-size: 12pt;\">Dear {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">We have prepared the following invoice for you: <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Invoice status</strong>: {invoice_status}</span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Please contact us for more information.</span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2232, 'ticket', 'new-ticket-opened-admin', 'english', 'New Ticket Opened (Opened by Staff, Sent to Customer)', 'New Support Ticket Opened', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">New support ticket has been opened.</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Subject:</strong> {ticket_subject}</span><br /><span style=\"font-size: 12pt;\"><strong>Department:</strong> {ticket_department}</span><br /><span style=\"font-size: 12pt;\"><strong>Priority:</strong> {ticket_priority}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}<br /><br /></a>Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 0, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2233, 'ticket', 'ticket-reply', 'english', 'Ticket Reply (Sent to Customer)', 'New Ticket Reply', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">You have a new ticket reply to ticket <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket Subject:</strong> {ticket_subject}</span><br /><br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 0, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2234, 'ticket', 'ticket-autoresponse', 'english', 'New Ticket Opened - Autoresponse', 'New Support Ticket Opened', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">Thank you for contacting our support team. A support ticket has now been opened for your request. You will be notified when a response is made by email.</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Subject:</strong> {ticket_subject}</span><br /><span style=\"font-size: 12pt;\"><strong>Department</strong>: {ticket_department}</span><br /><span style=\"font-size: 12pt;\"><strong>Priority:</strong> {ticket_priority}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2235, 'invoice', 'invoice-payment-recorded', 'english', 'Invoice Payment Recorded (Sent to Customer)', 'Invoice Payment Recorded', '<span style=\"font-size: 12pt;\">Hi</span><br /><br /><span style=\"font-size: 12pt;\">Payment recorded for invoice <strong># {invoice_number}</strong></span><br /><br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2236, 'invoice', 'invoice-overdue-notice', 'english', 'Invoice Overdue Notice', 'Invoice Overdue Notice - {invoice_number}', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">This is an overdue notice for invoice <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\">This invoice was due: {invoice_duedate}</span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
(2237, 'invoice', 'invoice-already-send', 'english', 'Invoice Already Sent to Customer', 'On your command here is the invoice', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">At your request, here is the invoice with number <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Please contact us for more information.</span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 52, 0, 90, '2018-01-24 02:28:23', NULL, NULL),
