(3654, 'projects', 'invite-venue', 'english', 'Account owner approves invite', 'Event invitation for {project_type} on {project_date}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is an event planned on {project_date} for which we would like you to be part of. As a venue, we would like your company to provide your services for this event. Event details below:<br/><br/>{project_details}<br/><br/>Please click on the <a href=\"{invite_link}\">View Invite</a> to view the details and Accept / Decline the invite. If you have any questions or concerns, please do not hesitate to contact at:<br/><br/>Name: {invited_name}<br/><br/>Phone: {invited_phone}<br/><br/>Email: {invited_email}<br/><br/>Sincerely,<br/><br/>{invited_name}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 67, 0, 27, '2018-03-23 02:03:09', NULL, NULL),
(3655, 'client', 'new-client-created', 'english', 'New Contact Added/Registered (Welcome Email)', 'Welcome aboard', 'Dear {contact_firstname} {contact_lastname}<br /><br />Thank you for registering on the <strong>{companyname}</strong> CRM System.<br /> <br />We just wanted to say welcome.<br /> <br />Please contact us if you need any help.<br /> <br />Click here to view your profile: <a href=\"{crm_url}\">{crm_url}</a><br /> <br />Kind Regards, <br />{email_signature}<br /> <br />(This is an automated email, so please don\'t reply to this email address)', '{companyname} | SimplyIDo', '', 0, 1, 0, 68, 0, 27, '2018-03-23 02:03:47', NULL, NULL),
(3656, 'invoice', 'invoice-send-to-client', 'english', 'Send Invoice to Customer', 'Invoice with number {invoice_number} created', '<span style=\"font-size: 12pt;\">Dear {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">We have prepared the following invoice for you: <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Invoice status</strong>: {invoice_status}</span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Please contact us for more information.</span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 68, 0, 27, '2018-03-23 02:03:47', NULL, NULL),
(3657, 'ticket', 'new-ticket-opened-admin', 'english', 'New Ticket Opened (Opened by Staff, Sent to Customer)', 'New Support Ticket Opened', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">New support ticket has been opened.</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Subject:</strong> {ticket_subject}</span><br /><span style=\"font-size: 12pt;\"><strong>Department:</strong> {ticket_department}</span><br /><span style=\"font-size: 12pt;\"><strong>Priority:</strong> {ticket_priority}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}<br /><br /></a>Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 0, 0, 68, 0, 27, '2018-03-23 02:03:47', NULL, NULL),
