(3876, 'projects', 'invite-new-collaborator', 'english', 'New Invite', 'Collaborator invite pending approval for {project_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is a collaborator invite pending your approval. Details below:<br /><br /><bold>Collaborator details:</bold><br/><br/>Name: {vendor_name}<br/><br/>Phone: {vendor_phone}<br/><br/>Email: {vendor_email}<br/><br/>Tag: {vendor_tag}<br/><br/>Inviting Collaborator/Team Member details:<br/><br/>Name: {invited_name}Email: {invited_email}<br/><br/>{project_details}<br/><br/>Please click on the \"View Invite\" button to view the details and Accept / Decline the invite. <a href=\"{invite_link}\">View Invite</a> {email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 70, 0, 27, '2018-03-23 02:04:57', NULL, NULL),
(3877, 'projects', 'invite-collaborator', 'english', 'Account owner approves invite', 'Event invitation for {project_type} on {project_date}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is an event planned on {project_date} for which we would like you to be part of. As a collaborator, we would like your company to provide your services for this event. Event details below:<br/><br/>{project_details}<br/><br/>Please click on the <a href=\"{invite_link}\">View Invite</a> to view the details and Accept / Decline the invite. If you have any questions or concerns, please do not hesitate to contact at:<br/><br/>Name: {invited_name}<br/><br/>Phone: {invited_phone}<br/><br/>Email: {invited_email}<br/><br/>Sincerely,<br/><br/>{invited_name}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 70, 0, 27, '2018-03-23 02:04:57', NULL, NULL);
