(4036, 'venue', 'new-venue-added', 'english', 'New Venue Added', 'Pending Approval for {venue_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> New Venue has been added. Venue details below:<br/><br/>{venue_details}<br/><br/>Please click on the <a href=\"{venue_link}\">View Venue</a> to view the details and Accept the venue. <br/><br/>Sincerely,<br/><br/>{staff_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 72, 0, 27, '2018-03-23 02:15:34', NULL, NULL),
(4037, 'venue', 'venue-added', 'english', 'Venue Added', '{venue_name} added on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {venue_name} has been added on Simply I Do. Venue details below:<br/><br/>{venue_details}<br/><br/>If you have any questions or concerns, please do not hesitate to contact at {contact_details}. <br/><br/>Sincerely,<br/><br/>{staff_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 72, 0, 27, '2018-03-23 02:15:34', NULL, NULL),
