(4041, 'subscription-reminder', 'subscription-reminder', 'english', 'Subscription Reminder', '{event_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> This is reminder that your subscription is going to expire tomorrow. Subscription details below:<br/><br/>{subscription_details}<br/><br/><a href=\"{subscription_link}\">View Subscription</a> <br/><br/>Sincerely, Simply I Do{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 72, 0, 27, '2018-03-23 02:15:34', NULL, NULL),
(4042, 'projects', 'invite-new-venue', 'english', 'New Invite', 'Venue invite pending approval for {project_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is a venue invite pending your approval. Details below:<br /><br /><bold>Venue details:</bold><br/><br/>Name: {venue_name}<br/><br/>Phone: {venue_phone}<br/><br/>Email: {venue_email}<br/><br/><br/><br/>Inviting Vendor/Team Member details:<br/><br/>Name: {invited_name}Email: {invited_email}<br/><br/>{project_details}<br/><br/>Please click on the \"View Invite\" button to view the details and Accept / Decline the invite. <a href=\"{invite_link}\">View Invite</a> {email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 72, 0, 27, '2018-03-23 02:15:34', NULL, NULL),
