(4545, 'task-reminder', 'task-reminder', 'english', 'Task Reminder', '{task_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {name} This is reminder that you have task due tomorrow. Task details below:<br/><br/>{task_details}<br/><br/><a href=\"{task_link}\">View Task</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL),
(4546, 'meeting-reminder', 'meeting-reminder', 'english', 'Meeting Reminder', '{meeting_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> This is reminder that you have meeting due tomorrow. Meeting details below:<br/><br/>{meeting_details}<br/><br/><a href=\"{meeting_link}\">View Meeting</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL),
(4547, 'event-reminder', 'event-reminder', 'english', 'Event Reminder', '{event_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {name} This is reminder that you have event due tomorrow. Event details below:<br/><br/>{event_details}<br/><br/><a href=\"{event_link}\">View Event</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL),
(4548, 'subscription-reminder', 'subscription-reminder', 'english', 'Subscription Reminder', '{event_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> This is reminder that your subscription is going to expire tomorrow. Subscription details below:<br/><br/>{subscription_details}<br/><br/><a href=\"{subscription_link}\">View Subscription</a> <br/><br/>Sincerely, Simply I Do{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL),
(4549, 'projects', 'invite-new-venue', 'english', 'New Invite', 'Venue invite pending approval for {project_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is a venue invite pending your approval. Details below:<br /><br /><bold>Venue details:</bold><br/><br/>Name: {venue_name}<br/><br/>Phone: {venue_phone}<br/><br/>Email: {venue_email}<br/><br/><br/><br/>Inviting Vendor/Team Member details:<br/><br/>Name: {invited_name}Email: {invited_email}<br/><br/>{project_details}<br/><br/>Please click on the \"View Invite\" button to view the details and Accept / Decline the invite. <a href=\"{invite_link}\">View Invite</a> {email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL),
(4550, 'projects', 'decline-venue', 'english', 'Venue declines invite', 'Event invitation declined by {venue_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> {venue_name} has declined your invite for:<br/><br/>{project_details}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 78, 0, 112, '2018-03-29 02:59:37', NULL, NULL);
