(5246, 'venue', 'venue-added', 'english', 'Venue Added', '{venue_name} added on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {venue_name} has been added on Simply I Do. Venue details below:<br/><br/>{venue_details}<br/><br/>If you have any questions or concerns, please do not hesitate to contact at {contact_details}. <br/><br/>Sincerely,<br/><br/>{staff_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:50', NULL, NULL),
(5247, 'task-reminder', 'task-reminder', 'english', 'Task Reminder', '{task_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {name} This is reminder that you have task due tomorrow. Task details below:<br/><br/>{task_details}<br/><br/><a href=\"{task_link}\">View Task</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:50', NULL, NULL),
(5248, 'meeting-reminder', 'meeting-reminder', 'english', 'Meeting Reminder', '{meeting_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> This is reminder that you have meeting due tomorrow. Meeting details below:<br/><br/>{meeting_details}<br/><br/><a href=\"{meeting_link}\">View Meeting</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:50', NULL, NULL),
(5249, 'event-reminder', 'event-reminder', 'english', 'Event Reminder', '{event_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi </span>,</h2> {name} This is reminder that you have event due tomorrow. Event details below:<br/><br/>{event_details}<br/><br/><a href=\"{event_link}\">View Event</a> <br/><br/>Sincerely,<br/><br/>{assigned_name}{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:51', NULL, NULL),
(5250, 'subscription-reminder', 'subscription-reminder', 'english', 'Subscription Reminder', '{event_name} reminder on Simply I Do', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> This is reminder that your subscription is going to expire tomorrow. Subscription details below:<br/><br/>{subscription_details}<br/><br/><a href=\"{subscription_link}\">View Subscription</a> <br/><br/>Sincerely, Simply I Do{email_signature}', '{companyname} | SimplyIDo', NULL, 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:51', NULL, NULL),
(5251, 'projects', 'invite-new-venue', 'english', 'New Invite', 'Venue invite pending approval for {project_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is a venue invite pending your approval. Details below:<br /><br /><bold>Venue details:</bold><br/><br/>Name: {venue_name}<br/><br/>Phone: {venue_phone}<br/><br/>Email: {venue_email}<br/><br/><br/><br/>Inviting Vendor/Team Member details:<br/><br/>Name: {invited_name}Email: {invited_email}<br/><br/>{project_details}<br/><br/>Please click on the \"View Invite\" button to view the details and Accept / Decline the invite. <a href=\"{invite_link}\">View Invite</a> {email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:51', NULL, NULL),
(5252, 'projects', 'decline-venue', 'english', 'Venue declines invite', 'Event invitation declined by {venue_name}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> {venue_name} has declined your invite for:<br/><br/>{project_details}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:51', NULL, NULL),
(5253, 'projects', 'invite-venue', 'english', 'Account owner approves invite', 'Event invitation for {project_type} on {project_date}', '<h2><span style=\"font-size: 14pt;\">Hi {name}</span>,</h2> There is an event planned on {project_date} for which we would like you to be part of. As a venue, we would like your company to provide your services for this event. Event details below:<br/><br/>{project_details}<br/><br/>Please click on the <a href=\"{invite_link}\">View Invite</a> to view the details and Accept / Decline the invite. If you have any questions or concerns, please do not hesitate to contact at:<br/><br/>Name: {invited_name}<br/><br/>Phone: {invited_phone}<br/><br/>Email: {invited_email}<br/><br/>Sincerely,<br/><br/>{invited_name}{email_signature}', '{companyname} | SimplyIDo', '', 0, 0, 0, 87, 0, 121, '2018-04-03 07:17:51', NULL, NULL),
(5254, 'client', 'new-client-created', 'english', 'New Contact Added/Registered (Welcome Email)', 'Welcome aboard', 'Dear {contact_firstname} {contact_lastname}<br /><br />Thank you for registering on the <strong>{companyname}</strong> CRM System.<br /> <br />We just wanted to say welcome.<br /> <br />Please contact us if you need any help.<br /> <br />Click here to view your profile: <a href=\"{crm_url}\">{crm_url}</a><br /> <br />Kind Regards, <br />{email_signature}<br /> <br />(This is an automated email, so please don\'t reply to this email address)', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5255, 'invoice', 'invoice-send-to-client', 'english', 'Send Invoice to Customer', 'Invoice with number {invoice_number} created', '<span style=\"font-size: 12pt;\">Dear {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">We have prepared the following invoice for you: <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Invoice status</strong>: {invoice_status}</span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Please contact us for more information.</span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5256, 'ticket', 'new-ticket-opened-admin', 'english', 'New Ticket Opened (Opened by Staff, Sent to Customer)', 'New Support Ticket Opened', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">New support ticket has been opened.</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Subject:</strong> {ticket_subject}</span><br /><span style=\"font-size: 12pt;\"><strong>Department:</strong> {ticket_department}</span><br /><span style=\"font-size: 12pt;\"><strong>Priority:</strong> {ticket_priority}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}<br /><br /></a>Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 0, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5257, 'ticket', 'ticket-reply', 'english', 'Ticket Reply (Sent to Customer)', 'New Ticket Reply', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">You have a new ticket reply to ticket <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket Subject:</strong> {ticket_subject}</span><br /><br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 0, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5258, 'ticket', 'ticket-autoresponse', 'english', 'New Ticket Opened - Autoresponse', 'New Support Ticket Opened', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">Thank you for contacting our support team. A support ticket has now been opened for your request. You will be notified when a response is made by email.</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Subject:</strong> {ticket_subject}</span><br /><span style=\"font-size: 12pt;\"><strong>Department</strong>: {ticket_department}</span><br /><span style=\"font-size: 12pt;\"><strong>Priority:</strong> {ticket_priority}</span><br /> <br /><span style=\"font-size: 12pt;\"><strong>Ticket message:</strong></span><br /><span style=\"font-size: 12pt;\">{ticket_message}</span><br /><br /><span style=\"font-size: 12pt;\">You can view the ticket on the following link: <a href=\"{ticket_url}\">#{ticket_id}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5259, 'invoice', 'invoice-payment-recorded', 'english', 'Invoice Payment Recorded (Sent to Customer)', 'Invoice Payment Recorded', '<span style=\"font-size: 12pt;\">Hi</span><br /><br /><span style=\"font-size: 12pt;\">Payment recorded for invoice <strong># {invoice_number}</strong></span><br /><br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5260, 'invoice', 'invoice-overdue-notice', 'english', 'Invoice Overdue Notice', 'Invoice Overdue Notice - {invoice_number}', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">This is an overdue notice for invoice <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\">This invoice was due: {invoice_duedate}</span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL),
(5261, 'invoice', 'invoice-already-send', 'english', 'Invoice Already Sent to Customer', 'On your command here is the invoice', '<span style=\"font-size: 12pt;\">Hi {contact_firstname} {contact_lastname}</span><br /> <br /><span style=\"font-size: 12pt;\">At your request, here is the invoice with number <strong># {invoice_number}</strong></span><br /> <br /><span style=\"font-size: 12pt;\">You can view the invoice on the following link: <a href=\"{invoice_link}\">{invoice_number}</a></span><br /> <br /><span style=\"font-size: 12pt;\">Please contact us for more information.</span><br /> <br /><span style=\"font-size: 12pt;\">Kind Regards,</span><br /><span style=\"font-size: 12pt;\">{email_signature}</span>', '{companyname} | SimplyIDo', '', 0, 1, 0, 88, 0, 122, '2018-04-04 01:40:22', NULL, NULL);
